import React from "react";
import DashboardUser from "./DashboardUser";

/*
  For demo, Admin uses the same UI but we can add admin-only controls:
  - Manage users, authorities, reply as admin, verified sign etc.
  In this simplified version, the admin will reuse DashboardUser with extra note.
*/

export default function DashboardAdmin({ user }){
  return (
    <div className="container">
      <h2>Admin Dashboard</h2>
      <p className="muted">You have admin controls (manage users, authorities, reply as verified). Use the UI below to view and act on reports.</p>
      <DashboardUser user={user} />
      <div style={{marginTop:20}} className="admin-panel card">
        <h3>Admin Panel (Simplified)</h3>
        <p>To actually edit or delete users and authorities, use the backend admin endpoints or a future UI here.</p>
      </div>
    </div>
  );
}
