import React, { useEffect, useState } from "react";
import { MapContainer, TileLayer, Marker, Popup, useMap, Polyline } from "react-leaflet";
import L from "leaflet";
import "leaflet/dist/leaflet.css";

delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: '/leaflet/marker-icon-2x.png',
  iconUrl: '/leaflet/marker-icon.png',
  shadowUrl: '/leaflet/marker-shadow.png'
});

const BettiahCenter = [26.8138, 84.5154]; // approx coordinates — adjust if you want

export default function MapView({ markers = [], height = "500px", fitToMarkers = true, route = null }){
  // "markers": [{id, lat, lng, title, popup}]
  return (
    <div className="map-container" style={{height}}>
      <MapContainer center={BettiahCenter} zoom={13} style={{height: "100%", width: "100%"}}>
        <TileLayer
          attribution='&copy; OpenStreetMap contributors'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />
        {markers.map(m => (
          <Marker key={m.id} position={[m.lat, m.lng]}>
            <Popup>
              <strong>{m.title}</strong><br/>
              <small>{m.popup}</small>
            </Popup>
          </Marker>
        ))}
        {route && route.geometry && <Polyline positions={route.geometry.coordinates.map(c=>[c[1], c[0]])} />}
      </MapContainer>
    </div>
  );
}
