// src/components/ReportForm.jsx
import React, { useCallback, useState } from "react";
import { v4 as uuidv4 } from "uuid";
import { MapContainer, TileLayer, Marker, useMapEvents } from "react-leaflet";
import api from "../api/api";

const BettiahCenter = [26.8138, 84.5154];

function ClickMap({ marker, setMarker }) {
  useMapEvents({
    click(e){
      setMarker([e.latlng.lat, e.latlng.lng]);
    }
  });
  return null;
}

export default function ReportForm({ onSave }){
  const [title, setTitle] = useState("");
  const [desc, setDesc] = useState("");
  const [category, setCategory] = useState("sanitation");
  const [area, setArea] = useState("");
  const [pincode, setPincode] = useState("");
  const [marker, setMarker] = useState(null);
  const [searching, setSearching] = useState(false);

  const geocode = useCallback(async (q) => {
    if(!q) return;
    setSearching(true);
    try {
      // use Nominatim for free geocoding
      const query = `${q} Bettiah`;
      const resp = await fetch(`https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(query)}&limit=5`);
      const arr = await resp.json();
      if(arr.length === 0){
        alert("No results found for that query.");
        setSearching(false);
        return;
      }
      const first = arr[0];
      setMarker([parseFloat(first.lat), parseFloat(first.lon)]);
      setArea(first.display_name || q);
      setSearching(false);
    } catch (e){
      console.error(e);
      setSearching(false);
      alert("Geocoding failed.");
    }
  }, []);

  async function handleSubmit(e){
    e.preventDefault();
    if(!marker) { alert("Please place a marker on the map or search area/pincode first."); return; }
    const [lat, lng] = marker;
    const report = {
      title, desc, category, area, pincode, lat, lng,
      createdBy: (localStorage.getItem("seva_user") ? JSON.parse(localStorage.getItem("seva_user")).username : "anonymous")
    };

    // auto-assign authority: try backend or localStorage list
    if(process.env.REACT_APP_API_BASE){
      // create on server (server auto-assigns according to earlier backend logic)
      try {
        const saved = await api.createReport(report);
        onSave(saved);
        // reset
        setTitle(""); setDesc(""); setArea(""); setPincode(""); setMarker(null);
        return;
      } catch(err){
        console.warn("API create failed", err);
        alert("Failed to save to server, saving locally instead.");
      }
    }

    // local fallback: match authority by substring in department or category
    const auths = JSON.parse(localStorage.getItem('seva_authorities') || '[]');
    let matched = auths.find(a => a.department && a.department.toLowerCase().includes(category));
    if(!matched && auths.length>0) matched = auths[0];
    report.assignedTo = matched ? matched.id : null;

    report.id = uuidv4();
    report.votes = 0;
    report.completedVotes = 0;
    report.comments = [];
    report.status = "open";
    report.deadline = null;
    report.createdAt = new Date().toISOString();

    // save locally
    const arr = JSON.parse(localStorage.getItem('seva_reports') || '[]');
    arr.unshift(report);
    localStorage.setItem('seva_reports', JSON.stringify(arr));
    onSave(report);
    setTitle(""); setDesc(""); setArea(""); setPincode(""); setMarker(null);
  }

  return (
    <div style={{display:'flex', gap:16, alignItems:'flex-start'}}>
      <form onSubmit={handleSubmit} style={{flex:1}}>
        <label>Title</label>
        <input required value={title} onChange={e=>setTitle(e.target.value)} />
        <label>Description</label>
        <textarea required value={desc} onChange={e=>setDesc(e.target.value)} />
        <label>Category</label>
        <select value={category} onChange={e=>setCategory(e.target.value)}>
          <option value="sanitation">Sanitation</option>
          <option value="roads">Roads / PWD</option>
          <option value="police">Police / Safety</option>
          <option value="health">Health</option>
          <option value="fire">Fire & Rescue</option>
        </select>
        <label>Area / Locality</label>
        <input value={area} onChange={e=>setArea(e.target.value)} placeholder="Type area name and press Search"/>
        <label>Pincode</label>
        <input value={pincode} onChange={e=>setPincode(e.target.value)} placeholder="e.g. 845438" />
        <div style={{display:'flex', gap:8, marginTop:8}}>
          <button type="button" className="btn-outline" onClick={()=> geocode(area || pincode)}>Search area / pincode</button>
        </div>
        <div className="form-row" style={{marginTop:12}}>
          <button className="btn" type="submit">Submit Report (marker required)</button>
        </div>
      </form>

      <div style={{width:420}}>
        <div className="map-container" style={{height:380}}>
          <MapContainer center={marker ? [marker[0], marker[1]] : BettiahCenter} zoom={13} style={{height:'100%', width:'100%'}}>
            <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" attribution="&copy; OpenStreetMap contributors" />
            <ClickMap marker={marker} setMarker={setMarker} />
            {marker && <Marker position={[marker[0], marker[1]]} />}
          </MapContainer>
        </div>
        <p className="muted" style={{marginTop:8}}>Click on the map to place a marker OR use the Search to geocode area/pincode. Report will only be saved if marker exists.</p>
      </div>
    </div>
  );
}
