import React, { useState } from "react";
import MapView from "./MapView";
import api from "../api/api";

export default function ReportView({ report, onClose, onComment, onAssign, user }){
  const [commentText, setCommentText] = useState("");
  const [route, setRoute] = useState(null);

  async function calcETA(){
    if(!report.lat || !report.lng) { alert("Report has no coordinates"); return; }
    if(!navigator.geolocation) { alert("Geolocation not available"); return; }
    navigator.geolocation.getCurrentPosition(async pos => {
      const start = [pos.coords.longitude, pos.coords.latitude];
      const end = [parseFloat(report.lng), parseFloat(report.lat)];
      if(process.env.REACT_APP_API_BASE){
        try {
          const data = await api.getRouteGeoJSON([start, end], "driving-car");
          setRoute(data.features[0]);
          return;
        } catch(err){
          console.error("ORS proxy failed", err);
        }
      }
      // Direct ORS call (may fail due to CORS)
      try{
        const resp = await fetch("https://api.openrouteservice.org/v2/directions/driving-car/geojson", {
          method: "POST",
          headers: {
            "Authorization": process.env.REACT_APP_ORS_KEY,
            "Content-Type": "application/json"
          },
          body: JSON.stringify({ coordinates: [start, end] })
        });
        const data = await resp.json();
        setRoute(data.features[0]);
      }catch(e){
        alert("Could not fetch route (CORS or key issue). Use backend ORS proxy.");
      }
    }, err => { alert("Allow location to calculate ETA"); });
  }

  function handleComment(){
    if(commentText.trim() === "") return;
    const comment = { author: user?.username || "anonymous", text: commentText };
    if(process.env.REACT_APP_API_BASE){
      api.commentReport(report.id, comment).then(()=> onComment(report.id, { ...comment, at: new Date().toISOString() })).catch(()=> onComment(report.id, { ...comment, at: new Date().toISOString() }));
    } else {
      onComment(report.id, { ...comment, at: new Date().toISOString() });
    }
    setCommentText("");
  }

  function googleMapsLink(){
    if(report.lat && report.lng) return `https://www.google.com/maps/dir/?api=1&destination=${report.lat},${report.lng}`;
    return "https://www.google.com/maps";
  }

  return (
    <div className="report-view card">
      <button className="close" onClick={onClose}>X</button>
      <h3>{report.title}</h3>
      <p>{report.desc}</p>
      <p><b>Category:</b> {report.category} | <b>Area:</b> {report.area} | <b>Pincode:</b> {report.pincode}</p>
      <p><b>Assigned To:</b> {report.assignedTo || "Not assigned"} | <b>Status:</b> {report.status}</p>
      <div>
        <button className="btn" onClick={calcETA}>Calculate ETA & Route (ORS)</button>
        <a className="btn-outline" href={googleMapsLink()} target="_blank" rel="noreferrer">Open in Google Maps</a>
      </div>
      <div style={{marginTop:12}}>
        <MapView markers={report.lat ? [{ id: report.id, lat: parseFloat(report.lat), lng: parseFloat(report.lng), title: report.title, popup: report.area }] : []} height="300px" route={route} />
      </div>

      <div className="comments">
        <h4>Comments</h4>
        {report.comments?.length ? report.comments.map((c,i)=>
          <div key={i} className="comment">
            <b>{c.author}</b> <small>{new Date(c.at).toLocaleString()}</small>
            <p>{c.text}</p>
          </div>
        ) : <p>No comments yet</p>}
        <div className="comment-form">
          <input placeholder="Write a comment..." value={commentText} onChange={e=>setCommentText(e.target.value)} />
          <button className="btn" onClick={handleComment}>Post</button>
        </div>
      </div>
    </div>
  );
}
