// src/components/ReportView.jsx
import React, { useState } from "react";
import MapView from "./MapView";
import api from "../api/api";

export default function ReportView({ report, onClose, onComment, user }){
  const [commentText, setCommentText] = useState("");
  const [route, setRoute] = useState(null);
  const [etaInfo, setEtaInfo] = useState(null);

  async function calcETA(){
    if(!report.lat || !report.lng){ alert("Report has no coordinates"); return; }
    if(!navigator.geolocation){ alert("Geolocation not available"); return; }

    navigator.geolocation.getCurrentPosition(async pos => {
      const start = [pos.coords.longitude, pos.coords.latitude];
      const end = [parseFloat(report.lng), parseFloat(report.lat)];

      try {
        if(process.env.REACT_APP_API_BASE){
          const data = await api.getRouteGeoJSON([start, end], "driving-car");
          const feat = data.features?.[0];
          setRoute(feat);
          // ORS returns summary with duration and distance
          setEtaInfo(feat?.properties?.summary || null);
          return;
        }
      } catch(e){
        console.warn("Backend ORS proxy failed", e);
      }

      // fallback: direct ORS (may be blocked by CORS) if key provided
      try {
        const resp = await fetch("https://api.openrouteservice.org/v2/directions/driving-car/geojson", {
          method: "POST",
          headers: {"Authorization": process.env.REACT_APP_ORS_KEY, "Content-Type":"application/json"},
          body: JSON.stringify({ coordinates: [start, end] })
        });
        const data = await resp.json();
        const feat = data.features?.[0];
        setRoute(feat);
        setEtaInfo(feat?.properties?.summary || null);
      } catch(e){
        console.error(e);
        alert("Could not compute route. Use backend ORS proxy or check ORS key/CORS.");
      }
    }, err => { alert("Allow location to calculate ETA"); });
  }

  function handleComment(){
    if(!commentText.trim()) return;
    const comment = { author: user?.username || "anonymous", text: commentText, at: new Date().toISOString() };
    if(process.env.REACT_APP_API_BASE){
      api.commentReport(report.id, comment).then(()=> onComment(report.id, comment)).catch(()=> onComment(report.id, comment));
    } else {
      onComment(report.id, comment);
    }
    setCommentText("");
  }

  const googleNav = report.lat && report.lng ? `https://www.google.com/maps/dir/?api=1&destination=${report.lat},${report.lng}&travelmode=driving` : "https://www.google.com/maps";

  return (
    <div className="report-view card">
      <button className="close" onClick={onClose}>X</button>
      <h3>{report.title}</h3>
      <p>{report.desc}</p>
      <p><b>Area:</b> {report.area} | <b>Pincode:</b> {report.pincode}</p>
      <p><b>Assigned to:</b> {report.assignedTo || "Not assigned"} | <b>Status:</b> {report.status}</p>

      <div style={{display:'flex', gap:8, marginTop:8}}>
        <button className="btn" onClick={calcETA}>Calculate ETA & Route</button>
        <a className="btn-outline" href={googleNav} target="_blank" rel="noreferrer">Open in Google Maps</a>
      </div>

      {etaInfo && (
        <div style={{marginTop:8}}>
          <p><b>Estimated travel:</b> {(etaInfo.distance/1000).toFixed(2)} km â€” {(etaInfo.duration/60).toFixed(0)} min</p>
        </div>
      )}

      <div style={{marginTop:12}}>
        <MapView markers={report.lat ? [{id:report.id, lat:parseFloat(report.lat), lng:parseFloat(report.lng), title:report.title, popup:report.area}] : []} height="300px" route={route} />
      </div>

      <div style={{marginTop:12}}>
        <h4>Comments</h4>
        {report.comments?.length ? report.comments.map(c =>
          <div key={c.id || c.at} className="comment">
            <b>{c.author}</b> <small>{new Date(c.at).toLocaleString()}</small>
            <p>{c.text}</p>
          </div>
        ) : <p>No comments yet</p>}
        <div style={{display:'flex', gap:8, marginTop:8}}>
          <input placeholder="Write a comment..." value={commentText} onChange={e=>setCommentText(e.target.value)} />
          <button className="btn" onClick={handleComment}>Post</button>
        </div>
      </div>
    </div>
  );
}
