// src/components/DashboardAuthority.jsx
import React, { useEffect, useState } from "react";
import MapView from "./MapView";
import api from "../api/api";

export default function DashboardAuthority({ user }){
  const [reports, setReports] = useState([]);

  useEffect(()=>{
    if(process.env.REACT_APP_API_BASE){
      api.getReports().then(setReports).catch(()=> setReports(JSON.parse(localStorage.getItem('seva_reports')||'[]')));
    } else {
      setReports(JSON.parse(localStorage.getItem('seva_reports')||'[]'));
    }
  }, []);

  async function claimReport(id){
    const confirmed = window.confirm("Claim this report for your authority?");
    if(!confirmed) return;
    const authorityId = user?.username || prompt("Enter your authority id");
    if(process.env.REACT_APP_API_BASE){
      await api.updateReport(id, { assignedTo: authorityId });
      const updated = await api.getReports();
      setReports(updated);
    } else {
      const arr = JSON.parse(localStorage.getItem('seva_reports')||'[]');
      const updated = arr.map(x=> x.id===id ? {...x, assignedTo: authorityId } : x);
      localStorage.setItem('seva_reports', JSON.stringify(updated));
      setReports(updated);
    }
  }

  const myAssigned = reports.filter(r => r.assignedTo === (user?.username || ""));

  return (
    <div className="container">
      <h2>Authority Dashboard</h2>
      <p className="muted">Authority: claim or act on reports assigned to your department.</p>

      <div className="card">
        <h3>Assigned Reports</h3>
        {myAssigned.length === 0 && <p>No reports assigned to you</p>}
        {myAssigned.map(r=> (
          <div key={r.id} className="card small" style={{marginBottom:8}}>
            <h4>{r.title}</h4>
            <p>{r.desc?.slice(0,120)}</p>
            <p><b>Area:</b> {r.area} | <b>Pincode:</b> {r.pincode}</p>
            <div className="row" style={{gap:8}}>
              <button className="btn" onClick={()=> claimReport(r.id)}>Claim / Confirm</button>
              <button className="btn-outline" onClick={()=>{
                const deadline = prompt("Set deadline (text/ISO):", r.deadline || "");
                if(deadline){
                  if(process.env.REACT_APP_API_BASE){
                    api.updateReport(r.id, { deadline }).then(()=> api.getReports().then(setReports));
                  } else {
                    const arr = JSON.parse(localStorage.getItem('seva_reports')||'[]');
                    const updated = arr.map(x=> x.id===r.id ? {...x, deadline} : x);
                    localStorage.setItem('seva_reports', JSON.stringify(updated));
                    setReports(updated);
                  }
                }
              }}>Set Deadline</button>
            </div>
          </div>
        ))}
      </div>

      <div style={{marginTop:12}} className="card">
        <h3>Map â€” reports</h3>
        <MapView markers={reports.filter(r=>r.lat && r.lng).map(r=>({id:r.id, lat:parseFloat(r.lat), lng:parseFloat(r.lng), title:r.title, popup:r.area}))} height="450px" />
      </div>
    </div>
  );
}
