import React, { useState } from "react";
import { v4 as uuidv4 } from "uuid";

export default function ReportForm({ onSave }){
  const [title, setTitle] = useState("");
  const [desc, setDesc] = useState("");
  const [category, setCategory] = useState("sanitation");
  const [area, setArea] = useState("");
  const [pincode, setPincode] = useState("");
  const [lat, setLat] = useState("");
  const [lng, setLng] = useState("");

  function handleSubmit(e){
    e.preventDefault();
    const report = {
      id: uuidv4(),
      title, desc, category, area, pincode,
      lat: lat || null, lng: lng || null,
      votes: 0,
      completedVotes: 0,
      comments: [],
      status: "open",
      createdAt: new Date().toISOString(),
      assignedTo: null,
      deadline: null
    };
    // call backend or pass up
    onSave(report);
    // reset
    setTitle(""); setDesc(""); setArea(""); setPincode(""); setLat(""); setLng("");
  }

  return (
    <div className="card form-card">
      <h3>Submit New Report</h3>
      <form onSubmit={handleSubmit}>
        <label>Title</label>
        <input required value={title} onChange={e=>setTitle(e.target.value)} />
        <label>Description</label>
        <textarea required value={desc} onChange={e=>setDesc(e.target.value)} />
        <label>Category</label>
        <select value={category} onChange={e=>setCategory(e.target.value)}>
          <option value="sanitation">Sanitation</option>
          <option value="roads">Roads / PWD</option>
          <option value="police">Police / Safety</option>
          <option value="health">Health</option>
          <option value="fire">Fire & Rescue</option>
        </select>
        <label>Area / Locality</label>
        <input value={area} onChange={e=>setArea(e.target.value)} />
        <label>Pincode</label>
        <input value={pincode} onChange={e=>setPincode(e.target.value)} />
        <label>Latitude (optional)</label>
        <input value={lat} onChange={e=>setLat(e.target.value)} placeholder="eg 26.81" />
        <label>Longitude (optional)</label>
        <input value={lng} onChange={e=>setLng(e.target.value)} placeholder="eg 84.51" />
        <div className="form-row">
          <button className="btn" type="submit">Submit Report</button>
        </div>
      </form>
    </div>
  );
}
