// src/components/DashboardAdmin.jsx
import React, { useEffect, useState } from "react";
import MapView from "./MapView";
import api from "../api/api";

export default function DashboardAdmin({ user }){
  const [reports, setReports] = useState([]);

  useEffect(()=>{
    if(process.env.REACT_APP_API_BASE){
      api.getReports().then(setReports).catch(()=> {
        setReports(JSON.parse(localStorage.getItem('seva_reports')||'[]'));
      });
    } else {
      setReports(JSON.parse(localStorage.getItem('seva_reports')||'[]'));
    }
  }, []);

  function refresh(){
    if(process.env.REACT_APP_API_BASE){
      api.getReports().then(setReports).catch(()=> setReports(JSON.parse(localStorage.getItem('seva_reports')||'[]')));
    } else {
      setReports(JSON.parse(localStorage.getItem('seva_reports')||'[]'));
    }
  }

  return (
    <div className="container">
      <h2>Admin Dashboard</h2>
      <p className="muted">Admin: see all reports, assign authorities, set deadlines and reply as verified.</p>

      <div style={{marginTop:12}} className="card">
        <h3>City Map — all live reports (Bettiah)</h3>
        <MapView markers={reports.filter(r=>r.lat && r.lng).map(r=>({id:r.id, lat:parseFloat(r.lat), lng:parseFloat(r.lng), title:r.title, popup:r.area}))} height="500px" />
      </div>

      <div style={{marginTop:18}} className="card">
        <h3>Reports</h3>
        <p className="muted">Click a report ID from the list (or open its view) to assign authority or reply.</p>
        <div>
          {reports.length === 0 && <p>No reports yet.</p>}
          {reports.map(r => (
            <div key={r.id} className="card small" style={{marginBottom:8}}>
              <h4>{r.title}</h4>
              <p>{r.desc?.slice(0,120)}...</p>
              <p><b>Category:</b> {r.category} | <b>Assigned:</b> {r.assignedTo || "—"}</p>
              <div className="row" style={{gap:8}}>
                <button className="btn" onClick={async ()=>{
                  const newDeadline = prompt("Set deadline (ISO or text):", r.deadline || "");
                  if(newDeadline != null){
                    if(process.env.REACT_APP_API_BASE){
                      await api.updateReport(r.id, { deadline: newDeadline });
                      refresh();
                    } else {
                      const arr = JSON.parse(localStorage.getItem('seva_reports')||'[]');
                      const updated = arr.map(x=> x.id===r.id ? {...x, deadline:newDeadline} : x);
                      localStorage.setItem('seva_reports', JSON.stringify(updated));
                      setReports(updated);
                    }
                  }
                }}>Set Deadline</button>

                <button className="btn-outline" onClick={async ()=>{
                  const authorityId = prompt("Assign to authority (enter authority id):", r.assignedTo || "");
                  if(authorityId != null){
                    if(process.env.REACT_APP_API_BASE){
                      await api.updateReport(r.id, { assignedTo: authorityId });
                      refresh();
                    } else {
                      const arr = JSON.parse(localStorage.getItem('seva_reports')||'[]');
                      const updated = arr.map(x=> x.id===r.id ? {...x, assignedTo:authorityId} : x);
                      localStorage.setItem('seva_reports', JSON.stringify(updated));
                      setReports(updated);
                    }
                  }
                }}>Assign</button>

                <button className="btn-outline" onClick={()=>{
                  const reply = prompt("Write admin reply (this will be shown as replied by Admin):");
                  if(reply){
                    const comment = { author:`Admin (${user?.username||'admin'})`, text: reply, at: new Date().toISOString(), adminReply: true };
                    if(process.env.REACT_APP_API_BASE){
                      api.commentReport(r.id, comment).then(()=> refresh());
                    } else {
                      const arr = JSON.parse(localStorage.getItem('seva_reports')||'[]');
                      const updated = arr.map(x=> x.id===r.id ? {...x, comments:[...(x.comments||[]), comment]} : x);
                      localStorage.setItem('seva_reports', JSON.stringify(updated));
                      setReports(updated);
                    }
                  }
                }}>Reply (Verified)</button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
