// frontend/src/components/ReportForm.jsx
import React, { useCallback, useState } from "react";
import { v4 as uuidv4 } from "uuid";
import { MapContainer, TileLayer, Marker, useMapEvents } from "react-leaflet";
import api from "../api/api";

const BettiahCenter = [26.8138, 84.5154];

function ClickMap({ setMarker, onMarkerPlaced }) {
  useMapEvents({
    click(e){
      const lat = Number(parseFloat(e.latlng.lat).toFixed(6));
      const lng = Number(parseFloat(e.latlng.lng).toFixed(6));
      setMarker([lat, lng]);
      if(onMarkerPlaced) onMarkerPlaced(lat, lng);
    }
  });
  return null;
}

/**
 * buildReadableArea(addressObj)
 * addressObj is the "address" field returned by Nominatim.
 * We pick the most specific meaningful fields in a sensible order.
 */
function buildReadableArea(addr = {}) {
  // priority order for locality: house_number+road, neighbourhood, suburb, locality, village, town, city_district, city, county
  const parts = [];
  if (addr.house_number && addr.road) parts.push(`${addr.house_number} ${addr.road}`);
  else if (addr.road) parts.push(addr.road);

  const localityOrder = ["neighbourhood", "suburb", "locality", "village", "town", "city_district", "city", "county"];
  for (const key of localityOrder) {
    if (addr[key] && !parts.includes(addr[key])) parts.push(addr[key]);
  }

  // state & country are lower priority but useful
  if (addr.state && !parts.includes(addr.state)) parts.push(addr.state);
  if (addr.country && !parts.includes(addr.country)) parts.push(addr.country);

  // join first two or three segments to keep it concise
  return parts.slice(0, 3).join(", ");
}

export default function ReportForm({ onSave }){
  const [title, setTitle] = useState("");
  const [desc, setDesc] = useState("");
  const [category, setCategory] = useState("sanitation");
  const [area, setArea] = useState("");
  const [pincode, setPincode] = useState("");
  const [marker, setMarker] = useState(null);
  const [searching, setSearching] = useState(false);

  // reverse geocode either via backend proxy or direct Nominatim
  const reverseGeocode = useCallback(async (lat, lon) => {
    try {
      let json = null;
      // backend proxy preferred (allows proper User-Agent header)
      const base = process.env.REACT_APP_API_BASE || "";
      if (base) {
        try {
          const url = `${base.replace(/\/$/, "")}/geocode/reverse?lat=${encodeURIComponent(lat)}&lon=${encodeURIComponent(lon)}`;
          const resp = await fetch(url);
          if (resp.ok) json = await resp.json();
          else {
            console.warn("Backend geocode returned", resp.status, await resp.text());
          }
        } catch (e) {
          console.warn("Backend geocode proxy failed:", e);
        }
      }

      // fallback to front-end direct Nominatim
      if (!json) {
        const nomUrl = `https://nominatim.openstreetmap.org/reverse?format=json&lat=${encodeURIComponent(lat)}&lon=${encodeURIComponent(lon)}&addressdetails=1`;
        const resp = await fetch(nomUrl, { headers: { "Accept-Language": "en" }});
        if (!resp.ok) {
          console.warn("Nominatim reverse failed", resp.status);
          return;
        }
        json = await resp.json();
      }

      const addr = json.address || {};
      const areaText = buildReadableArea(addr) || (json.display_name || "");
      setArea(areaText);
      if (addr.postcode) setPincode(addr.postcode);
      else {
        // try other fields: display_name may contain postcode at end; we don't parse aggressively
      }
      console.log("reverseGeocode -> area:", areaText, "postcode:", addr.postcode);
    } catch (err) {
      console.warn("reverseGeocode error", err);
    }
  }, []);

  // geocode by query (area or pincode) -> place marker (existing logic)
  const geocode = useCallback(async (q) => {
    if(!q) return;
    setSearching(true);
    try {
      const query = `${q} Bettiah`;
      const resp = await fetch(`https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(query)}&limit=5`);
      if(!resp.ok) throw new Error(`HTTP ${resp.status}`);
      const arr = await resp.json();
      if(!arr || arr.length === 0){
        alert("No results found for that query.");
        setSearching(false);
        return;
      }
      const first = arr[0];
      const lat = parseFloat(first.lat);
      const lon = parseFloat(first.lon);
      setMarker([lat, lon]);
      // call reverse to fill area/postcode more reliably
      await reverseGeocode(lat, lon);
      setSearching(false);
    } catch (e){
      console.error(e);
      setSearching(false);
      alert("Geocoding failed.");
    }
  }, [reverseGeocode]);

  async function handleSubmit(e){
    e.preventDefault();
    if(!marker) { alert("Please place a marker on the map or search area/pincode first."); return; }
    const [lat, lng] = marker;
    const report = {
      title, desc, category, area, pincode, lat, lng,
      createdBy: (localStorage.getItem("seva_user") ? JSON.parse(localStorage.getItem("seva_user")).username : "anonymous")
    };

    if(process.env.REACT_APP_API_BASE){
      try {
        const saved = await api.createReport(report);
        onSave(saved);
        setTitle(""); setDesc(""); setArea(""); setPincode(""); setMarker(null);
        alert("Report submitted to server");
        return;
      } catch(err){
        console.warn("API create failed", err);
        alert("Failed to save to server, will save locally.");
      }
    }

    // local fallback
    const auths = JSON.parse(localStorage.getItem('seva_authorities') || '[]');
    let matched = auths.find(a => a.department && a.department.toLowerCase().includes(category));
    if(!matched && auths.length>0) matched = auths[0];
    report.assignedTo = matched ? matched.id : null;

    report.id = uuidv4();
    report.votes = 0;
    report.completedVotes = 0;
    report.comments = [];
    report.status = "open";
    report.deadline = null;
    report.createdAt = new Date().toISOString();

    const arr = JSON.parse(localStorage.getItem('seva_reports') || '[]');
    arr.unshift(report);
    localStorage.setItem('seva_reports', JSON.stringify(arr));
    onSave(report);
    setTitle(""); setDesc(""); setArea(""); setPincode(""); setMarker(null);
    alert("Report saved locally");
  }

  // called when user clicks on map
  async function handleMarkerPlaced(lat, lng){
    await reverseGeocode(lat, lng);
  }

  return (
    <div className="report-form-wrapper">
      <form onSubmit={handleSubmit} className="report-form">
        <label>Title</label>
        <input required value={title} onChange={e=>setTitle(e.target.value)} />
        <label>Description</label>
        <textarea required value={desc} onChange={e=>setDesc(e.target.value)} />
        <label>Category</label>
        <select value={category} onChange={e=>setCategory(e.target.value)}>
          <option value="sanitation">Sanitation</option>
          <option value="roads">Roads / PWD</option>
          <option value="police">Police / Safety</option>
          <option value="health">Health</option>
          <option value="fire">Fire & Rescue</option>
        </select>
        <label>Area / Locality</label>
        <input value={area} onChange={e=>setArea(e.target.value)} placeholder="Type area name and press Search"/>
        <label>Pincode</label>
        <input value={pincode} onChange={e=>setPincode(e.target.value)} placeholder="e.g. 845438" />
        <div style={{display:'flex', gap:8, marginTop:8}}>
          <button type="button" className="btn-outline" onClick={()=>geocode(area || pincode)} disabled={searching}>
            {searching ? "Searching..." : "Search area / pincode"}
          </button>
        </div>

        <div className="form-row" style={{marginTop:12}}>
          <button className="btn" type="submit">Submit Report (marker required)</button>
        </div>
      </form>

      <div className="report-map">
        <div className="map-container" style={{height:380}}>
          <MapContainer center={marker ? [marker[0], marker[1]] : BettiahCenter} zoom={13} style={{height:'100%', width:'100%'}}>
            <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" attribution="&copy; OpenStreetMap contributors" />
            <ClickMap setMarker={setMarker} onMarkerPlaced={handleMarkerPlaced} />
            {marker && <Marker position={[marker[0], marker[1]]} />}
          </MapContainer>
        </div>
        <p className="muted" style={{marginTop:8}}>Click on the map to place a marker OR use Search to geocode area/pincode. Report will only be saved if marker exists.</p>
      </div>
    </div>
  );
}
